源码：
program snakes;
{$Mode ObjFPc}
{$Threading On}
{$M 102400,0,102400}
{$APPTYPE GUI}
uses
    graph,wincrt,math,dos;

type
    node        = record
        x,y     : shortint;
        dir     : byte;
    end;

    Tchar       = record
        ifZero  : boolean;
        main    : char;
    end;

    Tmode       = (normal,fight,infinite,adventure,exitp);

operator =(a,b:Tchar)c:boolean;
begin
    exit((a.ifZero = b.ifZero) and (a.main = b.main));
end;

operator +(a,b:node)c:node;
begin
    c.x:=a.x+b.x;
    c.y:=a.y+b.y;
end;

const
    maxPlyer    = 9;
    up          = 1;
    down        = 2;
    left        = 3;
    right       = 4;
    movment     : array[1..4] of node
                = ((x:0;y:-1),(x:0;y:1),(x:-1;y:0),(x:1;y:0));
    colors      : array[1..9] of word
                = (32,35,39,42,44,46,52,55,59);
    deTime      = 800{MS};

    kup         : Tchar
                = (ifZero:true;main:#72);
    kdown       : Tchar
                = (ifZero:true;main:#80);
    kenter      : Tchar
                = (ifZero:false;main:#13);

var
    debug       : boolean;

{-----------------------------------------------------------------Graph Part}
procedure putgrass(x,y,dir,color:integer);
begin
    setFillStyle(SolidFill,color);
    bar(x*24+1,y*24+1,x*24+24,y*24+24);
    setTextStyle(DefaultFont,0,2);
    setColor(2);
    outTextXY(x*24+6,y*24+6,'');
end;

procedure putsnake(x,y,dir,color:integer);
begin
    setFillStyle(SolidFill,color);
    bar(x*24+1,y*24+1,x*24+24,y*24+24);
    setFillStyle(SolidFill,15);
    bar(x*24+6,y*24+6,x*24+19,y*24+18);
end;

procedure putstone(x,y,dir,color:integer);
begin
    setFillStyle(SolidFill,color);
    bar(x*24+1,y*24+1,x*24+24,y*24+24);
end;

procedure putcolor(x,y,dir,color:integer);
begin
    setFillStyle(SolidFill,color);
    if dir=0 then
        bar(x*24+2,y*24+2,x*24+23,y*24+23)
    else
        bar(x*24+1,y*24+1,x*24+24,y*24+24);
end;

function loadKey():Tchar;
var
    ch      : char;
begin
    ch:=readkey;
    if ch = #0 then
        begin
            loadkey.ifZero:=true;
            loadKey.main:=readkey;
        end
    else
        begin
            loadkey.ifZero:=false;
            loadkey.main:=ch;
        end;
end;

function menu():Tmode;
const
    menus   :array[1..5] of string
            = ('Normal   ','Fight    ','Infinite ','Adventure','Exit     ');
var
    ch:Tchar;
    id:byte = 1;
begin
    setFillStyle(SolidFill,0);
    bar(180,269+30,490,299+5*30);
    setTextStyle(DefaultFont,0,3);
    setcolor(15);
    SetBkColor(0);
    setFillStyle(SolidFill,1);
    bar(180,269+id*30,490,299+id*30);
    OutTextXY(208,300,#175'Normal   '#174);
    OutTextXY(232,330,    'Fight    ');
    OutTextXY(232,360,    'Infinite ');
    OutTextXY(232,390,    'Adventure');
    OutTextXY(232,420,    'Exit     ');

    ch:=loadkey;
    while (ch  kenter) do
        begin
            if ch = kup then
                begin
                    setFillStyle(solidFill,0);
                    bar(180,269+id*30,490,299+id*30);
                    OutTextXY(232,270+id*30,menus[id]);
                    if id = 1 then
                        id:= 5
                    else
                        dec(id);
                    setFillStyle(solidFill,1);
                    bar(180,269+id*30,490,299+id*30);
                    OutTextXY(208,270+id*30,#175+menus[id]+#174);
                end
            else
            if ch = kdown then
                begin
                    setFillStyle(solidFill,0);
                    bar(180,269+id*30,490,299+id*30);
                    OutTextXY(232,270+id*30,menus[id]);
                    if id = 5 then
                        id:= 1
                    else
                        inc(id);
                    setFillStyle(solidFill,1);
                    bar(180,269+id*30,490,299+id*30);
                    OutTextXY(208,270+id*30,#175+menus[id]+#174);
                end;
            ch:=loadkey;
        end;
    case id of
        1:exit(normal);
        2:exit(fight);
        3:exit(infinite);
        4:exit(adventure);
        5:exit(exitp);
    end;
end;

procedure questions(id:word;var kup,kdown,kleft,kright:Tchar);
var
    i:integer;
    x1,y1,x2,y2,l:word;
    ch:char;
    s,t:string;
begin
    setFillStyle(solidFill,colors[id]);
    for i:= 1 to 10 do
        begin
            l:=trunc(500*sin(pi*i/20));
            x1:=320-l shr 1;
            x2:=x1+l;

            l:= l shr 1;
            y1:=240-l shr 1;
            y2:=y1+l;

            bar(x1,y1,x2,y2);
            delay(30);
        end;

    setTextStyle(DefaultFont,0,2);
    setColor(0);
    str(id,t);
    s:='Your ID is '+t;
    OutTextXY(150,220,s);
    s:='Press your key for up';
    OutTextXY(150,240,s);
    kup:=loadkey;

    x2:=570;
    y1:=238;
    y2:=264;
    for i:= 1 to 10 do
        begin
            l:=trunc(123*sin(pi*i/20));
            x1:=570-l;
            bar(x1,y1,x2,y2);
            delay(30);
        end;
    setcolor(15);
    s:='                   down';
    OutTextXY(150,240,s);
    kdown:=loadkey;

    for i:= 1 to 10 do
        begin
            l:=trunc(123*sin(pi*i/20));
            x1:=570-l;
            bar(x1,y1,x2,y2);
            delay(30);
        end;
    setcolor(0);
    s:='                   left';
    OutTextXY(150,240,s);
    kleft:=loadkey;

    for i:= 1 to 10 do
        begin
            l:=trunc(123*sin(pi*i/20));
            x1:=570-l;
            bar(x1,y1,x2,y2);
            delay(30);
        end;
    setcolor(15);
    s:='                   right';
    OutTextXY(150,240,s);
    kright:=loadkey;
end;

procedure putScore(id,score:word);
var
    s,t     : string;
begin
    setFillStyle(SolidFill,colors[id]);
    bar(481,30*id-30,640,30*id-1);
    setTextStyle(DefaultFont,0,2);
    s:='';
    str(id,t);
    s:=s+t+': ';
    str(score,t);
    while length(t) &lt; 4 do
        t:=&#039; &#039;+t;
    s:=s+t;
    setColor(0);
    outTextXY(485,30*id-27,s);
end;

procedure putLevel(id,score,now:word);
var
    s,t     : string;
begin
    setFillStyle(SolidFill,colors[id]);
    bar(481,30*id-30,640,30*id-1);
    setTextStyle(DefaultFont,0,1);
    s:=&#039;&#039;;
    str(id,t);
    s:=s+t+&#039;: &#039;;
    str(score,t);
    while length(t) &lt; 4 do
        t:=&#039; &#039;+t;
    s:=s+t;
    setColor(0);
    outTextXY(485,30*id-27,s);
    str(now,t);
    while length(t)  200 then
        e:=1;
    q[e]:=x;
    inc(c);
end;

function quene.del:node;
begin
    inc(b);
    if b &gt; 200 then
        b:=1;
    del:=q[b];
    dec(c);
end;

procedure quene.init();
begin
    b:=0;
    e:=0;
    c:=0;
end;

{------------------------------------------------------------------Game Part}
type
    snake       = object
        id      : word;
        keys    : array[1..4] of Tchar;
        color   : word;
        body    : quene;
        dir     : shortint;
        score   : word;
        pos     : node;
        len     : byte;
        lastdir : byte;
        now     : byte;
        liven   : byte;
        procedure die();
        procedure init(NewID:word);
        procedure setKey(ch:Tchar);
    end;

    pnt         = ^snake;

var
    map         : array[0..19,0..19] of shortint;
    plrs        : array[0..10] of snake;
    pause       : boolean;
    live        : byte;
    mode        : Tmode;

procedure snake.init(NewID:word);
begin
    id:=Newid;
    body.init();
    questions(id,keys[up],keys[down],keys[left],keys[right]);
    body.add(pos);
end;

procedure snake.setKey(ch:Tchar);
var
    i       : byte;
begin
    for i:= 1 to 4 do
        if keys[i] = ch then
            if ((i-1) shr 1)  ((lastdir-1) shr 1) then
                dir:=i;
end;

procedure snake.die();
var
    t       : node;
begin
    while body.c &gt; 0 do
        begin
            t:=body.del;
            putgrass(t.x,t.y,t.dir,10);
            map[t.x,t.y]:=0;
        end;
    dec(live);
end;

function bite(id,x,y:byte):byte;
var
    tmp:quene;
    len:byte;
    t:node;
begin
    tmp.init;
    len:=0;
    while true do
        begin
            t:=plrs[id].body.del;
            if (t.x = x) and (t.y = y) then
                break;
            inc(len);
            putgrass(t.x,t.y,t.dir,17);
            map[t.x,t.y]:=0;
        end;
    dec(plrs[id].len,len);
    dec(plrs[id].score,len*10);
    putscore(id,plrs[id].score);
    exit(len);
end;

function run(p:pointer):longint;
var
    t       : node;
    ts      : Pnt;
    tmp     : byte;
begin
    ts:=pnt(p);
    with ts^ do
        while true do
            begin
                if pause then
                  begin
                    delay(detime);
                    continue;
                  end;
                pos:=pos+movment[dir];
                pos.dir:=dir;
                if mode=adventure then
                    begin
                        if score&gt;=now*175 then
                            begin
                                inc(now);
                                putLevel(id,score,now);
                                len:=3;
                            end;
                    end;
                if map[pos.x,pos.y] = -1 then
                    begin
                        inc(score,10);
                        if modeadventure then
                            putScore(id,score);
                        if mode=adventure then
                            putLevel(id,score,now);
                        inc(len);
                    end
                else if map[pos.x,pos.y] = -2 then
                    begin
                        inc(score,20);
                        if modeadventure then
                            putScore(id,score);
                        if mode=adventure then
                            putLevel(id,score,now);
                        inc(len);
                    end
                else if map[pos.x,pos.y] = -3 then
                    begin
                        if score&lt;20 then
                          begin
                            die;
                            exit;
                          end;
                        if len&lt;=1 then
                          begin
                            die;
                            exit;
                          end;
                        dec(score,20);
                        if modeadventure then
                            putScore(id,score);
                        if mode=adventure then
                            putLevel(id,score,now);
                        dec(len);
                    end
                else
                if map[pos.x,pos.y] = -32 then
                    inc(liven)
                else
                if (mode=adventure) and (liven0) and (map[pos.x,pos.y]  -30) then
                    dec(liven)
                else
                if map[pos.x,pos.y] = -33 then
                    begin
                        putsnake(pos.x,pos.y,dir,colors[id]);

                        t:=body.del;
                        putgrass(t.x,t.y,t.dir,10);
                        map[t.x,t.y]:=0;

                        delay(detime*3);
                    end
                else
                if (map[pos.x,pos.y]  -30) then
                    case mode of
                        normal      :   begin die; exit; end;
                        fight       :   begin
                                            if (map[pos.x,pos.y] = id) or (map[pos.x,pos.y] = -2) then
                                                begin
                                                    die;
                                                    exit;
                                                end
                                            else
                                                begin
                                                    tmp:=bite(3-id,pos.x,pos.y);
                                                    inc(len,tmp);
                                                    inc(score,tmp*10);
                                                    putscore(id,score);
                                                end;
                                        end;
                        infinite,
                        adventure   :   begin
                                            if pos.x &lt;= 0 then
                                                begin
                                                    pos.x:=18;
                                                    if map[pos.x,pos.y] = 19 then
                                                begin
                                                    pos.x:=1;
                                                    if map[pos.x,pos.y] &lt; -4 then
                                                        begin
                                                            die;
                                                            exit;
                                                        end;
                                                end
                                            else
                                            if pos.y &lt;= 0 then
                                                begin
                                                    pos.y:=18;
                                                    if map[pos.x,pos.y] = 19 then
                                                begin
                                                    pos.y:=1;
                                                    if map[pos.x,pos.y]  len do
                    begin
                        t:=body.del;
                        putgrass(t.x,t.y,t.dir,10);
                        map[t.x,t.y]:=0;
                    end;

                lastdir:=dir;
                delay(detime);

            end;
end;

{------------------------------------------------------------------Main Part}
var
    nums        : byte;
    keyID       : TThreadID;

procedure init();
var
    gm,gd       : smallint;
begin
    if ParamCount &gt; 0 then
        debug:=(lowercase(ParamStr(1)) = 'debug');
    gd:=d8bit;
    gm:=m640x480;
    initGraph(gd,gm,'');
end;

procedure Welcome;
var
    ch      : char;
    i,j     : integer;
begin
    setFillStyle(SolidFill,0);
    bar(1,1,640,480);

    for i:=0 to 30 do
        for j:=0 to 20 do
            putsnake(i,j,0,colors[i mod 10+1]);

    setFillStyle(SolidFill,0);
    bar(90,90,580,180);

    setTextStyle(DefaultFont,0,15);
    setcolor(44);
    outTextXY(100,100,'Snake');

    delay(500);
    for i:=0 to 255 do
        begin
            setcolor(i);
            outTextXY(100,100,'     s');
        end;

    setcolor(44);
    outTextXY(100,100,'     s');

    setTextStyle(DefaultFont,0,10);
    setcolor(44);

    bar(215,180,455,270);
    bar(170,0,500,90);

    outTextXY(225,190,'V I');

    outTextXY(180,10,'Beta');

end;

function ask():byte;
var
    i:integer;
    x1,y1,x2,y2,l:word;
    ch:char;
begin
    setFillStyle(solidFill,14);
    Setcolor(0);
    for i:= 1 to 10 do
        begin
            l:=trunc(500*sin(pi*i/20));
            x1:=320-l shr 1;
            x2:=x1+l;

            l:= l shr 1;
            y1:=240-l shr 1;
            y2:=y1+l;

            bar(x1,y1,x2,y2);
            delay(30);
        end;
    setTextStyle(DefaultFont,0,2);
    outTextXY(100,220,'How many players?(1 to 9)');
    ch:=readkey;
    while not (ch in ['1'..'9']) do
        ch:=readkey;
    ask:=ord(ch)-48;
end;

procedure prepare(nums:byte);
var
    i,j:byte;
begin
    fillchar(plrs,sizeof(plrs),0);
    live:=nums;

    for i:= 0 to 19 do
        for j:= 0 to 19 do
            map[i,j]:=-5;
    for i:= 1 to 18 do
        for j:= 1 to 18 do
            map[i,j]:=0;

    if mode in [normal,infinite,adventure] then
        begin
            for i:= 1 to nums shr 1 do
                begin
                    plrs[i].pos.x:=2;
                    plrs[i].pos.y:=i*4-1;
                    plrs[i].pos.dir:=right;
                    plrs[i].id:=i;
                    plrs[i].color:=colors[i];
                    plrs[i].dir:=right;
                    plrs[i].len:=3;
                    plrs[i].score:=0;
                    plrs[i].init(i);
                    if mode=adventure then
                        plrs[i].now:=1;
                end;

            for i:= nums shr 1 + 1 to nums do
                begin
                    plrs[i].pos.x:=17;
                    plrs[i].pos.y:=(i-nums shr 1)*4-2;
                    plrs[i].pos.dir:=left;
                    plrs[i].id:=i;
                    plrs[i].color:=colors[i];
                    plrs[i].dir:=left;
                    plrs[i].len:=3;
                    plrs[i].score:=0;
                    plrs[i].init(i);
                    if mode=adventure then
                        plrs[i].now:=1;
                end;
        end
    else
        begin
            with plrs[1] do
                begin
                    pos.x:=2;
                    pos.y:=9;
                    pos.dir:=right;
                    id:=1;
                    color:=colors[1];
                    dir:=right;
                    len:=10;
                    score:=1000;
                    init(1);
                end;
            with plrs[2] do
                begin
                    pos.x:=17;
                    pos.y:=10;
                    pos.dir:=left;
                    id:=2;
                    color:=colors[2];
                    dir:=left;
                    len:=10;
                    score:=1000;
                    init(2);
                end;
        end;
end;

procedure draw();
var
    i,j,k:integer;
begin
    setFillStyle(solidFill,0);
    bar(1,1,640,480);
    for i:= 0 to 19 do
        for j:= 0 to 19 do
            putgrass(i,j,0,10);
    for i:= 0 to 19 do
        putstone(0,i,0,7);
    for i:= 1 to 19 do
        putstone(i,0,0,7);
    for i:= 1 to 19 do
        putstone(19,i,0,7);
    for i:= 1 to 19 do
        putstone(i,19,0,7);
    randomize;
    for i:= 1 to 15 do
        begin
            randomize;
            j:=random(12)+4;
            randomize;
            k:=random(12)+4;
            while map[j,k]0 do
                begin
                    randomize;
                    j:=random(12)+4;
                    randomize;
                    k:=random(12)+4;
                end;
            putstone(j,k,0,7);
            map[j,k]:=-29;
        end;
    for i:= 1 to live do
        begin
            if modeadventure then
              putScore(i,plrs[i].score);
            if mode=adventure then
              putLevel(i,plrs[i].score,plrs[i].now);
            putsnake(plrs[i].pos.x,plrs[i].pos.y,plrs[i].dir,colors[i]);
        end;
end;

procedure DoPause;
begin
    pause:=not pause;
    if pause then
        begin
            setFillStyle(solidFill,0);
            setFillStyle(solidFill,0);
            setTextStyle(DefaultFont,0,4);
            setcolor(14);
            bar(483,330,640,480);
            OutTextXY(485,360,'PAUSE');
        end
    else
        begin
            setFillStyle(solidFill,0);
            setFillStyle(solidFill,0);
            setTextStyle(DefaultFont,0,14);
            setcolor(14);
            bar(483,330,640,480);
            OutTextXY(485,330,'GO');
        end;
end;

function CatchKey(p:pointer):longint;
var
    ch      : Tchar;
    i       : byte;
begin
    while true do
        begin
            ch:=LoadKey;
            if not ch.ifZero then
                if ch.main = #27 then
                    DoPause();
            if pause then continue;
            for i:= 1 to 9 do
                plrs[i].setKey(ch);
        end;
end;

procedure start;
var
    i:byte;
begin
    keyID:=BeginThread(@CatchKey);
    pause:=true;
    for i:= 1 to live do
        begin
            BeginThread(@run,@plrs[i]);
            delay(trunc(detime/live));
        end;

    setFillStyle(solidFill,0);
    setTextStyle(DefaultFont,0,14);
    setcolor(14);

    if not debug then
        begin

    OutTextXY(530,330,'3');
    delay(1000);

    bar(530,330,640,480);
    OutTextXY(530,330,'2');
    delay(1000);

    bar(530,330,640,480);
    OutTextXY(530,330,'1');
    delay(1000);

        end;

    bar(530,330,640,480);
    OutTextXY(485,330,'GO');

    pause:=false;
end;

procedure work();
var
    x,y:byte;
begin
    while live &gt; 0 do
        begin
            if pause then continue;
            randomize;
            x:=random(17)+1;
            randomize;
            y:=random(17)+1;
            while map[x,y]  0 do
                begin
                    randomize;
                    x:=random(17)+1;
                    randomize;
                    y:=random(17)+1;
                end;
            map[x,y]:=-1;
            apple(x,y);
            randomize;
            if random(2)=0 then
            if mode=adventure then
              begin
                randomize;
                case random(10) of
                  0,1:begin
                        pear(x,y);
                        map[x,y]:=-2;
                      end;
                  2,3:begin
                        milk(x,y);
                        map[x,y]:=-3;
                      end;
                  4:begin
                      dlive(x,y);
                      map[x,y]:=-32;
                    end;
                  5:begin
                      turn(x,y);
                      map[x,y]:=-33;
                    end;
                end;
              end;
            delay(random(5000));

            if debug then
                writeln(live);
        end;
    killThread(KeyID);
end;

procedure swap(var a,b:snake);
var
    t:snake;
begin
    t:=a;
    a:=b;
    b:=t;
end;

procedure sort();
var
    i,j:byte;
begin
    for i:= 1 to nums do
        for j:= 1 to i do
            if plrs[i].score &gt; plrs[j].score then
                swap(plrs[i],plrs[j]);
end;

procedure finish;
var
    i,x1,x2,y1,y2,l:word;
    t,s:string;
begin
    setFillStyle(solidFill,14);
    for i:= 1 to 18 do
        begin
            l:=trunc(500*sin(pi*i/20));
            x1:=320-l shr 1;
            x2:=x1+l;

            l:= l shr 1;
            y1:=240-l shr 1;
            y2:=y1+l;

            bar(x1,y1,x2,y2);
            delay(30);
        end;
    setTextStyle(DefaultFont,0,6);
    setColor(0);
    OutTextXY(100,150,'GAME OVER');

    sort;
    SetTextStyle(DefaultFont,0,2);
    for i:= 1 to min(3,nums) do
        begin
            setFillStyle(solidFill,plrs[i].color);
            bar(250,240+(i-1)*30,390,240+i*30);
            s:='Player ';
            str(plrs[i].id,t);
            s:=s+t;
            str(plrs[i].score,t);
            s:=s+' '+t;
            OutTextXY(260,243+(i-1)*30,s);
        end;
    SetTextStyle(DefaultFont,0,1);
    outTextXY(100,350,'Want To Try Again?(Y/N)');
end;

begin
    init;
    welcome;
    mode:=menu();
    if mode=exitp then
      halt;
    if mode in [normal,infinite,adventure] then
        nums:=ask
    else
        nums:=2;
    prepare(nums);
    draw;
    start;
    work;
    finish;
    if loadkey.main in ['y','Y'] then
      begin
        closeGraph;
        exec('snakes.exe','');
      end;
end.

